/**
 * Funding method type.
 */
export type FundingMethod = 'card' | 'payment-request' | 'exchange' | 'wallets' | 'manual';
/**
 * Funding provider type.
 */
export type FundingProvider = 'coinbase' | 'moonpay';
/**
 * Payment option type.
 */
export type PaymentOption = {
    method: FundingMethod;
    provider: FundingProvider;
};
